pub enum ReadEntryResult<M: ReadEntry, Entry = MultipartField<M>> {
    Entry(Entry),
    End(M),
    Error(M, io::Error),
}

pub enum PartialReason {
    CountLimit,
    SizeLimit,
    IoError(io::Error),
    Utf8Error(str::Utf8Error),
}

pub enum SaveResult<Success, Partial> {
    Full(Success),
    Partial(Partial, PartialReason),
    Error(io::Error),
}

